import Recipes from './main/Recipes'
import RegisterRecipes from './main/RegisterRecipes'
import Settings from './main/Settings'
import Support from './main/Support'

export default {
  name: 'Home',
  components: {
    ReditusLogo, Recipes, RegisterRecipes, Settings, Support
  },
  data: function () {
    return {
      mainElements: {
        recipesActive: false,
        registerActive: false,
        settingsActive: false,
        supportActive: false
      }
    }
  },
  methods: {
    hideAllElements () {
      const self = this
      Object.keys(self.mainElements).forEach(el => { self.mainElements[el] = false })
    },
    redirect (link) {
      window.location.href = link
    }
  },
  beforeMount () {
    this.mainElements.recipesActive = true
  }
}
</script>

